config.description:
{"config":{
"session_id": {
"type": "string",
"description": "必填, uuid, 会话的唯一标识"
},
"add_punc": {
"type": "integer",
"description": "是否加标点开关，0或1, 默认1",
"default": 1,
"options": [0, 1]
},
"itn_switch": {
"type": "integer",
"description": "是否反正则化，0或1, 默认0",
"default": 0,
"options": [0, 1]
},
"vad_switch": {
"type": "integer",
"description": "音频断点检测，0或1，默认1，长音频（时长超过30s）需要开启",
"default": 1,
"options": [0, 1]
},
"diarization": {
"type": "integer",
"description": "说话人日志开关，0或1, 默认0",
"default": 0,
"options": [0, 1]
},
"spk_num": {
"type": "integer",
"description": "若能确认音频中的说话人数量，请填写此参数，以增加说话人日志的准确度"
},
"translate": {
"type": "integer",
"description": "是否进行英文翻译，0或1, 默认0（当输入的为中文或英文音频时才起效，其他语言均返回空字符串）",
"default": 0,
"options": [0, 1],
"note": "仅对中文或英文音频有效"
},
"sensitive": {
"type": "integer",
"description": "是否进行敏感词检测，0或1, 默认0",
"default": 0,
"options": [0, 1]
},
"language": {
"type": "string",
"description": "音频语言，可自动识别（如需指定该参数，可设置\"zh\"代表中文，\"en\"代表英文，其他语言映射代码请咨询项目负责人）",
"default": "auto",
"options": ["auto", "zh", "en"]
},
"audio_classification": {
"type": "integer",
"description": "是否做音频分类，0或1, 默认0",
"default": 0,
"options": [0, 1]
},
"diarization_mode": {
"type": "integer",
"description": "说话人日志参数，选项0、1或2，默认0。参数越大，说话人区分灵敏度越高",
"default": 0,
"options": [0, 1, 2]
},
"max_end_sil": {
"type": "integer",
"description": "尾点判停参数：尾部连续检测到多长时间静音进行判停，单位ms，建议参数范围[300,1000]，默认800ms",
"default": 800,
"range": [300, 1000]
},
"max_single_seg": {
"type": "integer",
"description": "强制截断参数：超过多长时间有声段会进行强制截断，单位ms，参数范围[5000,30000]，默认30000ms",
"default": 30000,
"range": [5000, 30000]
},
"speech_noise_thres": {
"type": "integer",
"description": "取值越大，语音被误判为噪音的概率越大，参数范围[0,1]，默认值0.6",
"default": 0.6,
"range": [0, 1]
}
}
}
